use Livewire\WithFileUploads; new class extends Component
{ use WithFileUploads; // Profile selection public ?int $current_profile_id = null; public bool $isCreatingNew = false; // Profile fields public ?string $profile_name = null; public string $display_mode = 'combined'; public string $profile_type = 'individual'; public string $slug = ''; public ?string $title = null; public ?string $company = null; public ?string $business_name = null; public ?string $tax_id = null; public ?string $bio = null; public ?string $phone = null; public ?string $email = null; public ?string $website = null; public ?string $address = null; public bool $is_public = true; // Image uploads public ?TemporaryUploadedFile $profile_image = null; public ?TemporaryUploadedFile $cover_image = null; // Social links public array $socialLinks = []; // Display fields public ?string $existing_profile_image = null; public ?string $existing_cover_image = null; public bool $showPreview = false; public bool $showForm = true; /** * Mount the component. */ public function mount(?int $profileId = null): void { /** @var User $user */ $user = Auth::user(); // If profileId is provided, load that profile for editing if ($profileId) { $profile = $user->profiles()->findOrFail($profileId); $this->loadProfile($profile); $this->isCreatingNew = false; $this->showForm = true; } else { // No profileId means we're creating a new profile $this->isCreatingNew = true; $this->current_profile_id = null; $this->slug = Profile::generateUniqueSlug($user->name, $user->id); $this->profile_name = 'My Profile'; $this->display_mode = 'combined'; $this->profile_type = 'individual'; $this->showForm = true; } } /** * Load profile data into component. */ protected function loadProfile(Profile $profile): void { $this->current_profile_id = $profile->id; $this->isCreatingNew = false; $this->profile_name = $profile->profile_name; $this->display_mode = $profile->display_mode ?? 'combined'; $this->profile_type = $profile->profile_type ?? 'individual'; $this->slug = $profile->slug; $this->title = $profile->title; $this->company = $profile->company; $this->business_name = $profile->business_name; $this->tax_id = $profile->tax_id; $this->bio = $profile->bio; $this->phone = $profile->phone; $this->email = $profile->email; $this->website = $profile->website; $this->address = $profile->address; $this->is_public = $profile->is_public; $this->existing_profile_image = $profile->profile_image; $this->existing_cover_image = $profile->cover_image; // Load social links $this->socialLinks = $profile->socialLinks->map(function ($link) { return [ 'id' => $link->id, 'platform' => $link->platform, 'label' => $link->label, 'url' => $link->url, 'icon' => $link->icon, 'order' => $link->order, ]; })->toArray(); } /** * Add a new social link. */ public function addSocialLink(): void { $this->socialLinks[] = [ 'id' => null, 'platform' => 'custom', 'label' => '', 'url' => '', 'icon' => '', 'order' => count($this->socialLinks), ]; } /** * Remove a social link. */ public function removeSocialLink(int $index): void { unset($this->socialLinks[$index]); $this->socialLinks = array_values($this->socialLinks); // Reindex array } /** * Update the profile. */ public function save(): void { /** @var User $user */ $user = Auth::user(); // Validate profile data $slugRule = ['required', 'string', 'max:255', 'regex:/^[a-z0-9-]+$/']; // Check slug uniqueness per user if ($this->current_profile_id) { $slugRule[] = \Illuminate\Validation\Rule::unique('profiles', 'slug') ->where('user_id', $user->id) ->ignore($this->current_profile_id); } else { $slugRule[] = \Illuminate\Validation\Rule::unique('profiles', 'slug') ->where('user_id', $user->id); } $validated = $this->validate([ 'profile_name' => ['required', 'string', 'max:255'], 'display_mode' => ['required', 'in:personal_only,company_only,combined'], 'profile_type' => ['required', 'in:individual,business'], 'slug' => $slugRule, 'title' => ['nullable', 'string', 'max:255'], 'company' => ['nullable', 'string', 'max:255'], 'business_name' => ['required_if:profile_type,business', 'nullable', 'string', 'max:255'], 'tax_id' => ['nullable', 'string', 'max:100'], 'bio' => ['nullable', 'string', 'max:5000'], 'phone' => ['nullable', 'string', 'max:255'], 'email' => ['nullable', 'email', 'max:255'], 'website' => ['nullable', 'url', 'max:500'], 'address' => ['nullable', 'string', 'max:1000'], 'is_public' => ['boolean'], 'profile_image' => ['nullable', 'image', 'max:2048'], 'cover_image' => ['nullable', 'image', 'max:5120'], 'socialLinks' => ['array'], 'socialLinks.*.platform' => ['required_with:socialLinks.*.url', 'string', 'max:255'], 'socialLinks.*.label' => ['required_with:socialLinks.*.url', 'string', 'max:255'], 'socialLinks.*.url' => ['required_with:socialLinks.*.label', 'url', 'max:500'], 'socialLinks.*.icon' => ['nullable', 'string', 'max:255'], ]); // Get or create profile if ($this->current_profile_id) { $profile = $user->profiles()->findOrFail($this->current_profile_id); } else { $profile = new Profile(['user_id' => $user->id]); // If this is the first profile, set it as primary if ($user->profiles()->count() === 0) { $validated['is_primary'] = true; } } // Handle profile image upload if ($this->profile_image) { if ($profile->profile_image) { Storage::disk('public')->delete($profile->profile_image); } $validated['profile_image'] = $this->profile_image->store('profiles', 'public'); } // Handle cover image upload if ($this->cover_image) { if ($profile->cover_image) { Storage::disk('public')->delete($profile->cover_image); } $validated['cover_image'] = $this->cover_image->store('profiles', 'public'); } // Remove image from validated if not uploaded unset($validated['profile_image'], $validated['cover_image'], $validated['socialLinks']); // Update or create profile $profile->fill($validated); $profile->save(); // Handle social links $existingLinkIds = collect($this->socialLinks)->pluck('id')->filter()->toArray(); // Delete removed links $profile->socialLinks()->whereNotIn('id', $existingLinkIds)->delete(); // Update or create social links foreach ($this->socialLinks as $index => $linkData) { if (empty($linkData['url']) || empty($linkData['label'])) { continue; // Skip empty links } $linkData['order'] = $index; $linkData['profile_id'] = $profile->id; if ($linkData['id']) { $profile->socialLinks()->where('id', $linkData['id'])->update( collect($linkData)->except('id')->toArray() ); } else { unset($linkData['id']); $profile->socialLinks()->create($linkData); } } // Refresh existing images $profile->refresh(); $this->existing_profile_image = $profile->profile_image; $this->existing_cover_image = $profile->cover_image; $this->current_profile_id = $profile->id; $this->isCreatingNew = false; // Clear temporary uploads $this->profile_image = null; $this->cover_image = null; // Redirect to edit route after save session()->flash('status', 'Profile saved successfully!'); $this->redirect(route('profile.builder.edit', $profile->id), navigate: true); } /** * Delete the current profile. */ public function deleteProfile(): void { if (!$this->current_profile_id) { return; } /** @var User $user */ $user = Auth::user(); $profile = $user->profiles()->findOrFail($this->current_profile_id); // Don't allow deleting if it's the only profile if ($user->profiles()->count() <= 1) { session()->flash('error', 'You cannot delete your only profile. Please create another profile first.'); return; } // If deleting primary, set another as primary if ($profile->is_primary) { $otherProfile = $user->profiles()->where('id', '!=', $profile->id)->first(); if ($otherProfile) { $otherProfile->setAsPrimary(); } } $profile->delete(); session()->flash('status', 'Profile deleted successfully!'); // Redirect to index after deletion $this->redirect(route('profile.builder.index'), navigate: true); } /** * Toggle preview mode. */ public function togglePreview(): void { $this->showPreview = !$this->showPreview; }
